/**
 * @fileoverview A script to generate simplified trading signals from an array of
 * processed data items.
 *
 * This script is designed to process an array of stock data, including trading
 * recommendations and current position details. It determines whether to generate
 * an executable order (BUY or SELL) based on predefined logic and account
 * status. The code has been updated to include a buying power check to prevent
 * the 'insufficient qty' error and now also checks effective buying power.
 *
 * @param {Array<Object>} items The input array of data items. Each item is
 * an object containing details like ticker, trading recommendation, account
 * information, and current position data. The account data is assumed to be
 * the same across all items.
 * @returns {Array<Object>} An array of trading signal objects, with a format
 * suitable for direct use by the Alpaca API. Returns an empty array if no
 * executable signals are generated or if trading is blocked.
 */

// Simplified Trading Signals - Output Only Alpaca Order Fields
const items = $input.all();
const tradingSignals = [];
const currentTime = new Date().toISOString();

console.log(`üîç Starting with ${items.length} processed data items`);

if (items.length === 0) {
  console.log(`‚ùå No input data received`);
  return [];
}

// Extract account info from the first item (all items have same account data)
// This assumes the data structure is consistent. It handles both direct JSON and wrapped JSON.
const sampleItem = items[0]?.json || items[0];
const buyingPower = parseFloat(sampleItem?.account_buying_power || 0);
const effectiveBuyingPower = parseFloat(sampleItem?.account_effective_buying_power || 0);
const tradingBlocked = sampleItem?.account_trading_blocked || false;
const accountBlocked = sampleItem?.account_blocked || false;

console.log(`üí∞ Buying Power: $${buyingPower.toFixed(2)}`);
console.log(`üí∞ Effective Buying Power: $${effectiveBuyingPower.toFixed(2)}`);
console.log(`üö´ Trading Blocked: ${tradingBlocked}, Account Blocked: ${accountBlocked}`);

// Early exit if trading is blocked on the account or effective buying power is 0
if (tradingBlocked || accountBlocked) {
  console.log(`üö´ Trading is blocked - no orders to generate`);
  return [];
}

if (effectiveBuyingPower <= 0) {
  console.log(`üö´ Effective buying power is $${effectiveBuyingPower.toFixed(2)} - no buy orders will be generated`);
}

let executableSignals = 0;

// Process each item in the input array
for (const item of items) {
  // Handle both direct data and data nested under a 'json' key
  const data = item.json || item;

  // Skip if the item is invalid or missing a ticker
  if (!data || !data.ticker) {
    console.log(`‚ö†Ô∏è Skipping invalid item (no ticker found)`);
    continue;
  }

  // Destructure and normalize key data points from the input item
  const ticker = data.ticker.toUpperCase();
  const sentimentScore = parseFloat(data.sentiment_score || 0);
  const tradingRecommendation = data.trading_recommendation || 'HOLD';
  const recommendedPositionSize = parseInt(data.recommended_position_size || 1);
  const hasCurrentPosition = data.has_current_position || false;
  const currentPositionQty = parseFloat(data.current_position_qty || 0);
  const plPercentage = parseFloat(data.current_position_pl_percentage || 0);

  // IMPORTANT: Assuming a 'current_price' field exists in the data.
  // This is crucial for a buying power check.
  const stockPrice = parseFloat(data.current_price || 0);

  console.log(`\nüìã Processing: ${ticker} - Sentiment Score: ${sentimentScore.toFixed(2)}`);
  console.log(`    Position: ${hasCurrentPosition ? `${currentPositionQty} shares (${plPercentage.toFixed(1)}% P/L)` : 'None'}`);
  console.log(`    Current Price: $${stockPrice.toFixed(2)}`);

  let finalSide = null;
  let finalQty = 0;

  // Determine executable orders based on the sentiment score
  // A positive sentiment score indicates a potential BUY signal
  if (sentimentScore > 0.2) {
    // Check if effective buying power allows for buy orders
    if (effectiveBuyingPower <= 0) {
      console.log(`    ‚ùå Skipping BUY: Effective buying power is $${effectiveBuyingPower.toFixed(2)}`);
    } else if (hasCurrentPosition && currentPositionQty < 0) {
      // Positive sentiment on a SHORT position: This means cover the short.
      finalSide = 'buy';
      finalQty = Math.min(Math.abs(currentPositionQty), recommendedPositionSize, 50);

      // Before creating the order, perform a buying power check
      const estimatedCost = finalQty * stockPrice;
      if (effectiveBuyingPower < estimatedCost) {
          console.log(`    ‚ùå Skipping BUY: Insufficient effective buying power ($${effectiveBuyingPower.toFixed(2)}) for estimated cost ($${estimatedCost.toFixed(2)})`);
          finalSide = null; // Clear the signal
          finalQty = 0;
      } else {
        console.log(`    üü¢ BUY TO COVER: ${finalQty} shares of short position (positive sentiment)`);
      }
    } else if (hasCurrentPosition && currentPositionQty > 0) {
      // Positive sentiment on an existing LONG position: Add to the position.
      if (effectiveBuyingPower >= 500 && Math.abs(currentPositionQty) < 50) {
        finalSide = 'buy';
        finalQty = Math.min(recommendedPositionSize, 25);

        // Before creating the order, perform a buying power check
        const estimatedCost = finalQty * stockPrice;
        if (effectiveBuyingPower < estimatedCost) {
            console.log(`    ‚ùå Skipping BUY: Insufficient effective buying power ($${effectiveBuyingPower.toFixed(2)}) for estimated cost ($${estimatedCost.toFixed(2)})`);
            finalSide = null;
            finalQty = 0;
        } else {
          console.log(`    üü¢ ADD TO POSITION: ${finalQty} shares (positive sentiment)`);
        }
      } else {
        console.log(`    ‚è∏Ô∏è NO ACTION: Conditions not met to add to position (Effective Buying Power: $${effectiveBuyingPower.toFixed(2)}, Current Qty: ${currentPositionQty})`);
      }
    } else if (!hasCurrentPosition && effectiveBuyingPower >= 500) {
      // Positive sentiment with no existing position: Create a new LONG position.
      finalSide = 'buy';
      finalQty = Math.min(recommendedPositionSize, 35);

      // Before creating the order, perform a buying power check
      const estimatedCost = finalQty * stockPrice;
      if (effectiveBuyingPower < estimatedCost) {
          console.log(`    ‚ùå Skipping BUY: Insufficient effective buying power ($${effectiveBuyingPower.toFixed(2)}) for estimated cost ($${estimatedCost.toFixed(2)})`);
          finalSide = null;
          finalQty = 0;
      } else {
        console.log(`    üü¢ NEW BUY: ${finalQty} shares (positive sentiment)`);
      }
    }
  } 
  // A negative sentiment score indicates a potential SELL signal
  else if (sentimentScore < -0.2) {
