function formatQuantity(qty) {
  const num = Math.abs(parseFloat(qty));
  if (isNaN(num) || num <= 0) return null;

  // Alpaca requires integer quantities for shares
  return Math.floor(num).toString();
}

// Helper function to validate symbol
function isValidSymbol(symbol) {
  return symbol && typeof symbol === 'string' && symbol.trim().length > 0;
}

// Helper function to map order types
function mapOrderType(orderType) {
  const type = (orderType || '').toLowerCase();
  switch (type) {
    case 'market':
      return 'market';
    case 'limit':
      return 'limit';
    case 'stop':
      return 'stop';
    case 'stop_limit':
      return 'stop_limit';
    default:
      return 'market'; // Default to market order
  }
}

// Helper function to map time in force
function mapTimeInForce(tif) {
  const timeInForce = (tif || '').toLowerCase();
  switch (timeInForce) {
    case 'day':
      return 'day';
    case 'gtc':
      return 'gtc';
    case 'ioc':
      return 'ioc';
    case 'fok':
      return 'fok';
    default:
      return 'gtc'; // Default to GTC
  }
}

